                    if( $parsed_url && isset($parsed_url['path']) ) {

                        $uri = $parsed_url['path'];

                        // Force trailing slash
                        if( strlen($uri) > 1 && $uri[ strlen($uri)-1 ] != '/' && $uri[ strlen($uri)-1 ] != '*' )
                            $uri .= '/';

                        if( isset($parsed_url['query']) ) {
                            $uri .= "?{$parsed_url['query']}";
                        }

                        if( !in_array($uri, $excluded_urls) )
                            $excluded_urls[] = $uri;

